/**
 * Decrements its argument.
 *
 * @func
 * @memberOf R
 * @since v0.9.0
 * @category Math
 * @sig Number -> Number
 * @param {Number} n
 * @return {Number} n - 1
 * @see R.inc
 * @example
 *
 *      R.dec(42); //=> 41
 */
declare const dec: dec_0;
type dec_0 = {
    (n: number): dec_1;
};
type dec_1 = number;
export = dec;
